mapper.select:
select *
  from tomato
 where date(tomato_date) = #{tomatoDate}
   and tomato_idx = #{tomatoIdx}

mapper.update:
update tomato
   set tomato_name = #{tomatoName},
       tomato_full_regular = #{tomatoFullRegular},
       tomato_full_break = #{tomatoFullBreak}
 where tomato_idx = #{tomatoIdx}
